-- Traga os funcionários do sexo masculino ou os funcionarios que trabalham no jardim.




create database exercicio;

use exercicio;

create table funcionarios
  (
      idFuncionario integer,
      nome varchar(100),
      email varchar(200),
      sexo varchar(10),
      departamento varchar(100),
      admissao varchar(10),
      salario integer,
      cargo varchar(100),
      idRegiao int
  );


























--Traga os funcionarios que trabalhem no departamento de filmes OU no departamento de roupas

SELECT * FROM funcionarios
WHERE departamento = "FILMES"
OR departamento = "ROUPAS";

--O gestor de marketing pediu a lista das funcionarias (AS) = FEMININO que trabalhem no departamento 
--de filmes ou no departamento lar. Ele necessita enviar um email para as colaboradoras
--desses dois setores.

SELECT * FROM funcionarios
WHERE SEXO = "FEMININO" 
AND departamento IN("FILMES","LAR");

-- Traga os funcionários do sexo masculino ou os funcionarios que trabalham no jardim.

SELECT * FROM funcionarios
WHERE SEXO ="MASCULINO"
OR departamento ="JARDIM";